mapper.select:


    and gw_dev_code = #{gwDevCode}
     and device_name like concat('%', #{deviceName}, '%')
     and product_id = #{productId}
     and product_name like concat('%', #{productName}, '%')
     and user_id = #{userId}
     and user_name like concat('%', #{userName}, '%')
     and tenant_id = #{tenantId}
     and tenant_name like concat('%', #{tenantName}, '%')
     and serial_number = #{serialNumber}
     and status = #{status}
     and network_address like concat('%', #{networkAddress}, '%')
     and active_time between #{params.beginActiveTime} and #{params.endActiveTime}

order by create_time desc

select d.device_id, d.device_name, d.product_id, d.product_name, d.user_id, d.user_name, d.tenant_id, d.tenant_name,
d.serial_number,d.gw_dev_code, d.firmware_version, d.status,d.is_shadow,d.is_simulate ,d.location_way,d.active_time, d.img_url,a.device_id as auth_device_id
from iot_device d
left join iot_product_authorize a on a.device_id=d.device_id

     and ISNULL(a.device_id)
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_id = #{productId}
     and d.product_name like concat('%', #{productName}, '%')
     and d.user_id = #{userId}
     and d.user_name like concat('%', #{userName}, '%')
     and d.tenant_id = #{tenantId}
     and d.tenant_name like concat('%', #{tenantName}, '%')
     and d.serial_number = #{serialNumber}
    and d.gw_dev_code = #{gwDevCode,jdbcType=VARCHAR}
     and d.status = #{status}
     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}

order by d.create_time desc

select d.device_id, d.device_name, d.product_name, d.user_name, d.serial_number,d.gw_dev_code, d.firmware_version, d.status,d.rssi,d.is_shadow ,
d.location_way, d.active_time,d.network_address,d.longitude,d.latitude,max(u.is_owner) as is_owner
from iot_device d
inner join iot_device_user u on u.device_id = d.device_id

     and u.user_id = #{userId}
     and d.product_id = #{productId}
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_name like concat('%', #{productName}, '%')
     and d.serial_number = #{serialNumber}
     and d.gw_dev_code = #{gwDevCode}
     and d.status = #{status}
     and d.network_address like concat('%', #{networkAddress}, '%')
     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}
     and firmware_version = #{firmwareVersion}

group by d.device_id,d.user_id
order by d.create_time desc

select d.device_id, d.device_name, d.product_name,p.device_type, d.user_name, d.serial_number,d.gw_dev_code, d.firmware_version, d.status,d.rssi,d.is_shadow ,
       d.location_way, d.active_time,d.network_address,d.longitude,d.latitude,max(u.is_owner) as is_owner,
(select count(*) from iot_device d1 where d1.gw_dev_code = d.serial_number) as sub_device_count
from iot_device d
inner join iot_device_user u on u.device_id = d.device_id
left join iot_product p on p.product_id=d.product_id

     and u.user_id = #{userId}
     and d.tenant_id = #{tenantId}
     and d.product_id = #{productId}

group by d.device_id,d.user_id

select d.device_id, d.device_name, d.product_id, d.product_name,p.device_type,
    d.user_id, d.user_name, d.tenant_id, d.tenant_name, d.serial_number,d.gw_dev_code,
    d.firmware_version, d.status,d.rssi,d.is_shadow,d.is_simulate ,d.location_way,
    d.things_model_value, d.active_time,d.create_time,d.img_url,max(u.is_owner) as is_owner,
(select count(*) from iot_device d1 where d1.gw_dev_code = d.serial_number) as sub_device_count,
p.protocol_code,p.transport
from iot_device d
inner join iot_device_user u on u.device_id = d.device_id
left join iot_product p on p.product_id=d.product_id
 left join iot_device_group g on g.device_id=d.device_id 

     and g.group_id = #{groupId}
     and u.user_id = #{userId}
     and d.tenant_id = #{tenantId}
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_id = #{productId}
     and p.device_type = #{deviceType}
     and d.product_name like concat('%', #{productName}, '%')
     and d.user_name like concat('%', #{userName}, '%')
     and d.tenant_name like concat('%', #{tenantName}, '%')
     and d.serial_number = #{serialNumber}
     and d.gw_dev_code = #{gwDevCode}
     and d.status = #{status}
    and d.is_simulate = #{isSimulate}
     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}

group by d.device_id,d.user_id
order by d.create_time desc

select

    (select count(distinct d.device_id,d.user_id)
    from iot_device d
    inner join iot_device_user u on u.device_id = d.device_id

         and u.user_id = #{userId}
         and d.tenant_id = #{tenantId}

    ) as deviceCount,


    (select count(distinct d.device_id,d.user_id)
    from iot_device d
    inner join iot_device_user u on u.device_id = d.device_id

        d.status=3
         and u.user_id = #{userId}
         and d.tenant_id = #{tenantId}

    ) as deviceOnlineCount,

   (


       select count(distinct product_id)
       from iot_device
       where user_id = #{userId}



        select count(product_id)
        from iot_product

             and tenant_id = #{tenantId} 


    ) as productCount

mapper.update:
update iot_device
device_name = #{deviceName}, product_id = #{productId}, product_name = #{productName}, user_id = #{userId}, user_name = #{userName}, tenant_id = #{tenantId}, tenant_name = #{tenantName}, serial_number = #{serialNumber}, gw_dev_code = #{gwDevCode}, firmware_version = #{firmwareVersion}, status = #{status}, rssi = #{rssi}, is_shadow = #{isShadow}, is_simulate = #{isSimulate}, location_way = #{locationWay}, things_model_value = #{thingsModelValue}, network_address = #{networkAddress}, network_ip = #{networkIp}, longitude = #{longitude}, latitude = #{latitude}, active_time = #{activeTime}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, img_url = #{imgUrl}, summary = #{summary}, slave_id = #{slaveId},
where device_id = #{deviceId}

update iot_device
status = #{status}, network_address = #{networkAddress}, network_ip = #{networkIp}, longitude = #{longitude}, latitude = #{latitude}, active_time = #{activeTime}, update_time = #{updateTime,jdbcType=TIMESTAMP}
where serial_number = #{serialNumber} or gw_dev_code = #{serialNumber}

update iot_device
    set firmware_version = #{firmwareVersion,jdbcType=DECIMAL}
where serial_number = #{serialNumber,jdbcType=VARCHAR}

update iot_device
device_name = #{deviceName}, product_id = #{productId}, product_name = #{productName}, user_id = #{userId}, user_name = #{userName}, tenant_id = #{tenantId}, tenant_name = #{tenantName}, serial_number = #{serialNumber}, firmware_version = #{firmwareVersion}, status = #{status}, rssi = #{rssi}, is_shadow = #{isShadow}, is_simulate = #{isSimulate}, location_way = #{locationWay}, things_model_value = #{thingsModelValue}, network_address = #{networkAddress}, network_ip = #{networkIp}, longitude = #{longitude}, latitude = #{latitude}, active_time = #{activeTime}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, img_url = #{imgUrl}, summary = #{summary}, gw_dev_code = #{gwDevCode},
where serial_number = #{serialNumber}

mapper.delete:
delete from iot_device_group

     and device_id = #{deviceId}
     and group_id in(select group_id from iot_group where user_id = #{userId})

mapper.select:
select device_id, serial_number, device_name, user_id
from iot_device
where serial_number = #{deviceNumber}
